prompt --application/shared_components/plugins/dynamic_action/de_danielh_showwsnotify
begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(3380957875107401)
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'DE.DANIELH.SHOWWSNOTIFY'
,p_display_name=>'Show Websocket Notify'
,p_category=>'NOTIFICATION'
,p_supported_ui_types=>'DESKTOP'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('DYNAMIC ACTION','DE.DANIELH.SHOWWSNOTIFY'),'')
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'/*-------------------------------------',
' * Show Websocket Notify',
' * Version: 1.0 (18.06.2016)',
' * Author:  Daniel Hochleitner',
' *-------------------------------------',
'*/',
'FUNCTION render_show_ws_notify(p_dynamic_action IN apex_plugin.t_dynamic_action,',
'                               p_plugin         IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result IS',
'  --',
'  -- plugin attributes',
'  l_result           apex_plugin.t_dynamic_action_render_result;',
'  l_notify_icon      VARCHAR2(100) := p_dynamic_action.attribute_01;',
'  l_notify_wait_time NUMBER := p_dynamic_action.attribute_02;',
'  l_notify_position  VARCHAR2(100) := p_dynamic_action.attribute_03;',
'  l_logging          VARCHAR2(50) := p_dynamic_action.attribute_04;',
'  -- js file vars',
'  l_apexwsnotify_js    VARCHAR2(50);',
'  l_alertify_js        VARCHAR2(50);',
'  l_alertify_css       VARCHAR2(50);',
'  l_alertify_theme_css VARCHAR2(50);',
'  --',
'BEGIN',
'  -- attribute defaults',
'  l_notify_position  := nvl(l_notify_position,',
'                            ''bottom-right'');',
'  l_notify_wait_time := nvl(l_notify_wait_time,',
'                            0);',
'  l_logging          := nvl(l_logging,',
'                            ''false'');',
'  -- Debug',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action(p_plugin         => p_plugin,',
'                                          p_dynamic_action => p_dynamic_action);',
'    -- set js filenames (normal version)',
'    l_apexwsnotify_js    := ''showNotifyWebsocket'';',
'    l_alertify_js        := ''alertify'';',
'    l_alertify_css       := ''alertify'';',
'    l_alertify_theme_css := ''default'';',
'  ELSE',
'    -- minified version',
'    l_apexwsnotify_js    := ''showNotifyWebsocket.min'';',
'    l_alertify_js        := ''alertify.min'';',
'    l_alertify_css       := ''alertify.min'';',
'    l_alertify_theme_css := ''default.min'';',
'  END IF;',
'  --',
'  -- add Alertify and showNotifyWebsocket js files',
'  apex_javascript.add_library(p_name           => l_alertify_js,',
'                              p_directory      => p_plugin.file_prefix ||',
'                                                  ''js/'',',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  --',
'  apex_javascript.add_library(p_name           => l_apexwsnotify_js,',
'                              p_directory      => p_plugin.file_prefix ||',
'                                                  ''js/'',',
'                              p_version        => NULL,',
