,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'/*-------------------------------------',
' * Init Websocket Notify Connection',
' * Version: 1.0 (18.06.2016)',
' * Author:  Daniel Hochleitner',
' *-------------------------------------',
'*/',
'FUNCTION render_init_ws_connection(p_dynamic_action IN apex_plugin.t_dynamic_action,',
'                                   p_plugin         IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result IS',
'  --',
'  -- plugin attributes',
'  l_result              apex_plugin.t_dynamic_action_render_result;',
'  l_use_ssl             NUMBER := p_plugin.attribute_01;',
'  l_server_hostname     VARCHAR2(200) := p_dynamic_action.attribute_02;',
'  l_server_port         NUMBER := p_dynamic_action.attribute_03;',
'  l_websocket_room      VARCHAR2(100) := p_dynamic_action.attribute_04;',
'  l_websocket_userid    VARCHAR2(100) := p_dynamic_action.attribute_05;',
'  l_websocket_authtoken VARCHAR2(500) := p_dynamic_action.attribute_06;',
'  l_logging             VARCHAR2(50) := p_dynamic_action.attribute_07;',
'  -- js file vars',
'  l_apexwsnotify_js VARCHAR2(50);',
'  l_socketio_js     VARCHAR2(50);',
'  --',
'BEGIN',
'  -- attribute defaults',
'  l_logging := nvl(l_logging,',
'                   ''false'');',
'  -- Debug',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action(p_plugin         => p_plugin,',
'                                          p_dynamic_action => p_dynamic_action);',
'    -- set js filenames (normal version)',
'    l_apexwsnotify_js := ''initNotifyWebsocket'';',
'    l_socketio_js     := ''socket.io'';',
'  ELSE',
'    -- minified version',
'    l_apexwsnotify_js := ''initNotifyWebsocket.min'';',
'    l_socketio_js     := ''socket.io.min'';',
'  END IF;',
'  --',
'  -- add socket.io and initNotifyWebsocket js files',
'  apex_javascript.add_library(p_name           => l_socketio_js,',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  --',
'  apex_javascript.add_library(p_name           => l_apexwsnotify_js,',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  --',
'  --',
'  l_result.javascript_function := ''initNotifyWebsocket.initConnection'';',
'  l_result.attribute_01        := l_use_ssl;',
'  l_result.attribute_02        := l_server_hostname;',
'  l_result.attribute_03        := l_server_port;',
'  l_result.attribute_04        := l_websocket_room;',
'  l_result.attribute_05        := l_websocket_userid;',
'  l_result.attribute_06        := l_websocket_authtoken;',
'  l_result.attribute_07        := l_logging;',
'  --',
'  RETURN l_result;',
'  --',
'END render_init_ws_connection;'))
